CREATE FUNCTION IF NOT EXISTS `getFormFields`(in_table_name varchar(64)) RETURNS longtext CHARSET utf8mb4 COLLATE utf8mb4_general_ci
    READS SQL DATA
    COMMENT ' # getFormFields'
BEGIN 
    DECLARE result longtext default '[]';

    
    

    IF (
        NOT EXISTS(
            select * from INFORMATION_SCHEMA.tables
            WHERE table_schema = database()
            and table_name = 'bezug_config'
            
        )
    ) THEN 


    SELECT 
        concat('[',
            group_concat( F order by position SEPARATOR ',
            ')
        ,']')


    INTO result

    FROM 
    (


        SELECT 
            group_concat(
                    
                    concat('
                        {
                            "xtype":        "panel",
                            "bodyPadding":  5,
                            "items":        ',getFormFieldsSets(in_table_name,`field_path`),',
                            "title":        "',`field_path`,'",
                            "scrollable":   "y",
                            "layout":       "form",
                            "border":       false
                        }
                    ')
                    ORDER BY `field_path`
                    SEPARATOR ',
                    '
            ) F,
            -99999 position
        FROM 
        (
            SELECT
                `field_path`      
            FROM 
            (

                SELECT
                    `field_path`,
                    `position`        
                FROM 
                (
                    select 
                        left(`field_path`,IF (INSTR(`field_path`,'/')=0,255,INSTR(`field_path`,'/')-1)) `field_path`,
                        ds_column_form_label.`position`
                    from
                        ds_column_form_label
                        join ds on ds_column_form_label.table_name=ds.table_name
                        and ifnull(ds.alternativeformxtype,'')=''
                    where 
                        ds_column_form_label.`table_name` = in_table_name
                        and ds_column_form_label.`active` = 1
                        
                            

                    union

                    select 
                        left(`field_path`,IF (INSTR(`field_path`,'/')=0,255,INSTR(`field_path`,'/')-1)) `field_path`,
                        `position`
                    from
                        ds_column_tagfields
                    where 
                        `table_name` = in_table_name
                        and `active` = 1
                ) A
                ORDER BY `position`, `field_path`
            ) B
            GROUP BY `field_path`
        ) C 

        UNION

        SELECT
            concat('{
                "xtype": "',lower(ds.alternativeformxtype),'",
                "title": ',DOUBLEQUOTE(`title`),'
            }
            ') F,
            -999998 position
        from 
            ds  
        where 
            `table_name` = in_table_name
            and ifnull(ds.alternativeformxtype,'')<>''

        UNION

        SELECT
        concat('{
            "xtype": "cmp_ds_formtabpanel",
            "title": ',DOUBLEQUOTE(`title`),',
            "deferedPanel": {
                "xtype": "view-',lower(table_name),'",
                "bind": {
                    "record": "{currentrecord}"
                },
                "border": false,
                "collapsible": false,
                "referencedList": true,
                "referenced": ',lower(columnsdef),'
            }
        }
        ') F,
        position
        FROM (
         select
            ds.title,
            ds_reference_tables.*
        from

            ds_reference_tables
            join ds
            on ds_reference_tables.table_name = ds.table_name
            and ds.existsreal=1

            join ds_access
                on ds.table_name = ds_access.table_name 
                and ds_access.read=1
            join VIEW_SESSION_GROUPS on VIEW_SESSION_GROUPS.group = ds_access.role

        where
            reference_table_name=in_table_name
            and active = 1
        order by position
        ) REF



    ) X
    ;


    ELSE 


    SELECT 
        concat('[',
            group_concat( F   order by position SEPARATOR ',
            ')
        ,']')


    INTO result

    FROM 
    (


        SELECT 
            group_concat(
                    
                    concat('
                        {
                            "xtype":        "panel",
                            "bodyPadding":  5,
                            "items":        ',getFormFieldsSets(in_table_name,`field_path`),',
                            "title":        "',`field_path`,'",
                            "scrollable":   "y",
                            "layout":       "form",
                            "border":       false
                        }
                    ')
                    ORDER BY `field_path`
                    SEPARATOR ',
                    '
            ) F,
         -99999 position
        FROM 
        (
            SELECT
                `field_path`      
            FROM 
            (

                SELECT
                    `field_path`,
                    `position`        
                FROM 
                (
                    select 
                        left(`field_path`,IF (INSTR(`field_path`,'/')=0,255,INSTR(`field_path`,'/')-1)) `field_path`,
                        `position`
                    from
                        ds_column_form_label
                    where 
                        `table_name` = in_table_name
                        and `active` = 1
                        
                            

                    union

                    select 
                        left(`field_path`,IF (INSTR(`field_path`,'/')=0,255,INSTR(`field_path`,'/')-1)) `field_path`,
                        `position`
                    from
                        ds_column_tagfields
                    where 
                        `table_name` = in_table_name
                        and `active` = 1
                ) A
                ORDER BY `position`, `field_path`
            ) B
            GROUP BY `field_path`
        ) C


        UNION

        SELECT
        concat('{
            "xtype": "cmp_ds_formtabpanel",
            "title": ',DOUBLEQUOTE(`title`),',
            "deferedPanel": {
                "xtype": "view-',lower(table_name),'",
                "bind": {
                    "record": "{currentrecord}"
                },
                "border": false,
                "collapsible": true,
                "referencedList": true,
                "referenced": ',lower(columnsdef),'
            }
        }
        ') F,
        position
        FROM (
         select
            ds.title,
            ds_reference_tables.*
        from
            ds_reference_tables
            join ds
            on ds_reference_tables.table_name = ds.table_name
            and ds.existsreal=1
            join ds_access
                on ds.table_name = ds_access.table_name 
                and ds_access.read=1
            join VIEW_SESSION_GROUPS on VIEW_SESSION_GROUPS.group = ds_access.role
        where
            reference_table_name=in_table_name
            and active = 1
        order by position
            
        ) REF



        UNION

        SELECT
        concat('{
            "xtype": "cmp_ds_formtabpanel",
            "title": ',DOUBLEQUOTE(`title`),',
            "deferedPanel": {
                "xtype": "[{lowerprefix}]checklistview-',lower(referenced_table_name),'",
                "bind": {
                    "record": "{currentrecord}"
                },
                "border": false,
                /*"collapsible": true,*/
                "referencedList": true,
                "intermedia": ',DOUBLEQUOTE(lower(intermedia_table_name)),',
                "referenced": ',lower(table_name_json),',
                "referencedTo": ',lower(referenced_table_json),'
            }
        }
        ') F,
        99999 position
        FROM (

            select
              intermedia_table_name,
              max(table_name_json) table_name_json,
              max(constraint_name) constraint_name,
              max(referenced_table_name) referenced_table_name,
              max(referenced_table_json) referenced_table_json,
              max(label) title
            from
              ds_nm_tables
            where
              table_name = in_table_name
              and
              active=1
            group by
              intermedia_table_name
            order by
              position
            
        ) REF


        

        UNION


        SELECT 

            concat(
                '
                {
                    "xtype": "cmp_ds_formtabpanel",
                    "title": ',DOUBLEQUOTE(`name`),',
                    "deferedPanel": {
                        "xtype": "view-view_blg_list_',lower(tabellenzusatz),'",
                        "bind": {
                            "record": "{currentrecord}"
                        },
                        "border": false,
                        "collapsible": true,
                        "referencedList": true,
                        "referenced": ',lower(columnsdef),'
                    }
                }
                '
            ) F,
        999999 position

        FROM 
        (


  select
    
                concat('blg_',adress_bezug,'_',lower(blg_config.tabellenzusatz)) blg_table,
                lower(concat('{"view_blg_list_',lower(blg_config.tabellenzusatz),'__kundennummer":"',adress_bezug,'__',blg_config.bezug_id,'", "view_blg_list_',lower(blg_config.tabellenzusatz),'__kostenstelle":"',adress_bezug,'__',bezug_kst,'"}'))    columnsdef,
                blg_config.*



            from 
 
            blg_config 
 
            where 
                adress_bezug=in_table_name
                and bezug_anzeigen=1
                and
                concat( 'view_blg_list_',lower( tabellenzusatz ) ) in (
                select lower(table_name) from (
                    select table_name from ds_access where `role` in (SELECT `group` FROM VIEW_SESSION_GROUPS) and (`read`+`write`+`delete`+`append` > 0)
                ) g_dsa group by table_name
                )


           
                
        ) RPT



        UNION


        SELECT 

            concat(
                '
                {
                    "xtype": "cmp_ds_formtabpanel",
                    "title": ',DOUBLEQUOTE(`name`),',
                    "deferedPanel": {
                        "xtype": "',lower(`xtype`),'",
                        "bind": {
                            "record": "{currentrecord}"
                        },
                        "border": false,
                        "collapsible": true,
                        "referencedList": true,
                        "referenced": ',lower(columnsdef),'
                    }
                }
                '
            ) F,
        1900000+position position

        FROM 
            ds_extended_panels
        WHERE 
            table_name = in_table_name
            and active = 1 
            and FALSE




    ) X
    ;

    END IF;
    



        

    RETURN replaceDefaultDefinitionVars(in_table_name,result);
END ;;
