    READS SQL DATA
BEGIN
    DECLARE strDEF longtext;

    SELECT 
        group_concat(
            A.x
            SEPARATOR ',
            '
        )
        INTO strDEF
    FROM (
    SELECT
        
concat(
IF (NON_UNIQUE = 1,'' ,' UNIQUE '),
